document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".menu-toggle"),t=document.querySelector(".nav-list");e&&t&&e.addEventListener("click",(function(){t.classList.toggle("active"),document.body.classList.toggle("menu-open")}));const n=document.querySelector(".carousel-prev"),o=document.querySelector(".carousel-next"),c=document.querySelectorAll(".story-card");if(n&&o&&c.length>0){let a=0;function l(){c.forEach(((e,t)=>{e.style.display=t===a?"block":"none"}))}l(),n.addEventListener("click",(function(){a=(a-1+c.length)%c.length,l()})),o.addEventListener("click",(function(){a=(a+1)%c.length,l()}))}const r=document.querySelectorAll(".section-title, .feature-card, .quote-card, .story-card");r.length>0&&r.forEach((e=>{e.classList.add("fade-in")}));const d=document.querySelector(".quote-card .btn");d&&d.addEventListener("click",(function(){const e=document.querySelector(".quote-card blockquote p").textContent;if(navigator.share)navigator.share({title:"今日雅言 - 自在美丽",text:e,url:window.location.href}).catch((e=>{console.log("分享失败:",e),alert("分享功能暂时不可用，请稍后再试。")}));else{const t=document.createElement("textarea");t.value=e,document.body.appendChild(t),t.select(),document.execCommand("copy"),document.body.removeChild(t),alert("雅言已复制到剪贴板！")}}))}));